
import numpy as np
import matplotlib.pyplot as plt
def generate_labyrinth():
    labyrinth = np.zeros((64, 64), dtype=int)  # Initialize a matrix of zeros

    # Set walls
    labyrinth[0, :] = 1  # Top border
    labyrinth[-1, :] = 1  # Bottom border
    labyrinth[:, 0] = 1  # Left border
    labyrinth[:, -1] = 1  # Right border

    # Set entrance and exit
    labyrinth[0, 1] = 2  # Entrance
    labyrinth[-1, -2] = 3  # Exit

    return labyrinth.tolist()

