
import numpy as np
import matplotlib.pyplot as plt
import random
def generate_labyrinth(rows, cols):
    # Initialize the maze with walls
    maze = [[0 for _ in range(cols)] for _ in range(rows)]
    
    # Recursive backtracking algorithm to create paths
    def recursive_backtracking(row, col):
        directions = [(0, 2), (2, 0), (0, -2), (-2, 0)]
        random.shuffle(directions)

        for dr, dc in directions:
            next_row, next_col = row + dr, col + dc

            if 0 <= next_row < rows and 0 <= next_col < cols and maze[next_row][next_col] == 0:
                maze[row + dr // 2][col + dc // 2] = 1  # Mark the path
                maze[next_row][next_col] = 1  # Move to the next cell
                recursive_backtracking(next_row, next_col)

    # Start the recursive backtracking from the entrance
    recursive_backtracking(0, 1)
    iI = random.randint(0, 64)
    jI = random.randint(0, 64)
    eI = random.randint(0, 64)
    eJ = random.randint(0, 64)
    while maze[iI][jI]!=1 and maze[eI][eJ]!=1 and iI==eI and jI==eJ:
        iI = random.randint(0, 64)
        jI = random.randint(0, 64)
        eI = random.randint(0, 64)
        eJ = random.randint(0, 64)
    # Set entrance and exit
    maze[iI][jI] = 2  # Entrance
    maze[eI][eJ] = 3  # Exit
    
    return maze

